// ====================== POWERSHOT ======================
let powershotCounter = 0;
let powershotID = 0;
let powershotTrigger = false;
let ballColor = 0xFFCC00; // Color base de la pelota
let ballRadius = 6.4; // Ajustar seg√∫n el radio de la pelota (se puede ver en los archivos .hbs cada mapa)

function pointDistance(p1, p2) {
    let dx = p1.x - p2.x;
    let dy = p1.y - p2.y;
    return Math.sqrt(dx*dx + dy*dy);
}

function handleBallTouch() {
    let players = room.getPlayerList();
    let ballPosition = room.getBallPosition();
    let triggerDistance = ballRadius + 15 + 0.01; // 15 es el radio de los jugadores en la mayor√≠a de mapas, pero puede varias (ver .hbs)

    for (let i = 0; i < players.length; i++) {
        let player = players[i];
        if (!player.position) continue;

        let distanceToBall = pointDistance(player.position, ballPosition);

        if (distanceToBall < triggerDistance) {
            if (powershotID != player.id) {
                powershotID = player.id;
                powershotTrigger = false;
                powershotCounter = 0;
            } else {
                powershotCounter++;
                if (powershotCounter > 120 && !powershotTrigger) {
                    room.setDiscProperties(0, {invMass: 2.5});
                    room.setDiscProperties(0, {color: 0xFF4A04});
                    powershotTrigger = true;
                    room.sendAnnouncement("PowerShot cargado! üí•", player.id, 0xFF4A04);
                }
            }
        }

        if (distanceToBall > triggerDistance + 3 && player.id == powershotID && powershotTrigger) {
            powershotTrigger = false;
            powershotCounter = 0;
            powershotID = 0;
            if (Math.round(room.getDiscProperties(0).invMass * 100)/100 != 1) {
                room.setDiscProperties(0, {invMass: 1.5, color: ballColor});
            }
        }
    }
}

