// ====================== AFK KICK (20s) ======================
let lastAction = {};       // timestamp of last activity per player id
let prevPositions = {};    // last known position per player id (for movement detection)
let prevLastTouchId = null;
let warnedPlayers = {};    // players warned at 10s

const AFK_TIMEOUT = 19000;        // 20 segundos (aprox)
const WARNING_TIME = 10000;       // 10 segundos - aviso previo
const AFK_CHECK_INTERVAL = 2000;  // verificar cada 2s

// Inicializar timestamps para jugadores ya presentes
room.getPlayerList().forEach(p => {
    lastAction[p.id] = Date.now();
    if (p.position) prevPositions[p.id] = { x: p.position.x, y: p.position.y };
});

// Actualizar cuando el jugador realiza alguna actividad (teclas/movimiento)
room.onPlayerActivity = function(player) {
    lastAction[player.id] = Date.now();
    // si ya hab√≠a sido avisado, quitar el aviso
    if (warnedPlayers[player.id]) delete warnedPlayers[player.id];
};

// Funci√≥n que revisa movimiento/√∫ltimo toque de bal√≥n y expulsa si est√° AFK
function checkAfk() {
    const players = room.getPlayerList();

    // Actualizar por cambio de posici√≥n (movimiento del jugador)
    players.forEach(p => {
        if (!p || p.id === 0) return;
        if (p.position) {
            const prev = prevPositions[p.id];
            // Umbral peque√±o para considerar movimiento real
            if (!prev || Math.abs(prev.x - p.position.x) > 0.5 || Math.abs(prev.y - p.position.y) > 0.5) {
                lastAction[p.id] = Date.now();
                // si se movi√≥, quitar aviso previo
                if (warnedPlayers[p.id]) delete warnedPlayers[p.id];
            }
            prevPositions[p.id] = { x: p.position.x, y: p.position.y };
        }
        // Asegurar que exista registro
        if (!lastAction[p.id]) lastAction[p.id] = Date.now();
    });

    // Detectar si hubo un nuevo toque de bal√≥n (lastTouch se actualiza en otra parte del script)
    if (typeof lastTouch !== "undefined" && lastTouch && lastTouch.id !== prevLastTouchId) {
        lastAction[lastTouch.id] = Date.now();
        // quitar aviso si lo ten√≠a
        if (warnedPlayers[lastTouch.id]) delete warnedPlayers[lastTouch.id];
        prevLastTouchId = lastTouch.id;
    }

    // Evaluar inactividad, avisar a los 10s y kickear a los 20s
    const now = Date.now();
    players.forEach(p => {
        if (!p || p.id === 0) return;
        // No kickear/avisar espectadores ni administradores
        if (p.team === 0 || p.admin) return;

        const last = lastAction[p.id] || now;
        const idle = now - last;

        // Aviso a los 10 segundos si a√∫n no fue avisado
        if (idle >= WARNING_TIME && idle < AFK_TIMEOUT && !warnedPlayers[p.id]) {
            room.sendAnnouncement(`‚ö†Ô∏è ${p.name}, lleva ${Math.floor(idle/1000)}s sin moverte. Si no te mueves en 10s ser√°s expulsado por AFK.`, p.id, 0xFFAA00, "bold", 2);
            warnedPlayers[p.id] = Date.now();
        }

        // Kick a los AFK (20s)
        if (idle >= AFK_TIMEOUT) {
            try {
                room.kickPlayer(p.id, "AFK (20s de inactividad)", false);
            } catch (e) {
                // Ignorar errores al kickear
            }
            // Limpiar registros
            delete lastAction[p.id];
            delete prevPositions[p.id];
            if (warnedPlayers[p.id]) delete warnedPlayers[p.id];
        }
    });
}

// Iniciar comprobaci√≥n peri√≥dica
let afkInterval = setInterval(checkAfk, AFK_CHECK_INTERVAL);

// ====================== FUNCIONES AUXILIARES ======================
function countPlayersByTeam(team) {
    return room.getPlayerList().filter(p => p.team === team).length;
}

function autoAssignTeam(player) {
    let reds = countPlayersByTeam(1);
    let blues = countPlayersByTeam(2);
    let teamWithLess = reds <= blues ? 1 : 2;
    room.setPlayerTeam(player.id, teamWithLess);
    lastValidTeam[player.id] = teamWithLess;
}

function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
}

function mixTeams() {
    // Seleccionar solo jugadores activos (no espectadores) y excluyendo el posible id 0
    let players = room.getPlayerList().filter(p => p.id !== 0 && p.team !== 0);
    if (players.length < 2) return;

    let shuffled = shuffleArray(players);
    let half = Math.ceil(shuffled.length / 2);

    let reds = shuffled.slice(0, half);
    let blues = shuffled.slice(half);

    reds.forEach(p => {
        room.setPlayerTeam(p.id, 1);
        lastValidTeam[p.id] = 1;
    });
    blues.forEach(p => {
        room.setPlayerTeam(p.id, 2);
        lastValidTeam[p.id] = 2;
    });

    room.sendAnnouncement("üîÄ Jugadores mezclados al azar!", null, 0x00FFFF, "bold", 2);
}

function loadEmbeddedStadium(estadio) {
  room.setCustomStadium(estadio);
  room.setDefaultStadium("Custom");
  room.sendAnnouncement(
    "‚úÖ Estadio cargado correctamente (Custom).",
    null,
    0x00ff00,
    "bold",
    2
  );
  return true;
}

// ====================== AUTOBALANCE ======================
function rebalanceTeams() {
    let reds = room.getPlayerList().filter(p => p.team === 1);
    let blues = room.getPlayerList().filter(p => p.team === 2);

    let diff = Math.abs(reds.length - blues.length);
    if (diff < 2) {
        // Si ya no hay desbalance, cancelar balance pendiente
        if (pendingBalance) {
            clearTimeout(pendingBalance.timeout);
            pendingBalance = null;
        }
        return;
    }

    let bigger = reds.length > blues.length ? reds : blues;
    let smallerTeam = reds.length > blues.length ? 2 : 1;

    // Buscar el √∫ltimo jugador en ingresar del equipo m√°s grande
    let lastJoined = bigger.reduce((latest, p) => {
        if (!latest) return p;
        return (joinTimes[p.id] > joinTimes[latest.id]) ? p : latest;
    }, null);

    if (!lastJoined) return;

    // Si ya hay un balance en curso para ese mismo jugador, no repetir
    if (pendingBalance && pendingBalance.playerId === lastJoined.id) return;

    // Cancelar balance anterior
    if (pendingBalance) {
        clearTimeout(pendingBalance.timeout);
        pendingBalance = null;
    }

    // Aviso al jugador y a todos
    room.sendAnnouncement(
        `‚öñÔ∏è ${lastJoined.name} ser√° movido al equipo ${smallerTeam === 1 ? "Rojo" : "Azul"} en 10 segundos para equilibrar equipos.`,
        null, 0x00FFFF, "bold", 2
    );

    // Programar cambio de equipo en 10 segundos
    let timeout = setTimeout(() => {
        let stillInRoom = room.getPlayerList().find(p => p.id === lastJoined.id);
        let redsNow = countPlayersByTeam(1);
        let bluesNow = countPlayersByTeam(2);
        let diffNow = Math.abs(redsNow - bluesNow);

        if (stillInRoom && diffNow >= 2) {
            room.setPlayerTeam(lastJoined.id, smallerTeam);
            lastValidTeam[lastJoined.id] = smallerTeam;
            room.sendAnnouncement(
                `‚úÖ ${lastJoined.name} fue movido al equipo ${smallerTeam === 1 ? "Rojo" : "Azul"} para equilibrar.`,
                null, 0x00FF00, "bold", 2
            );
        }

        pendingBalance = null;
    }, 10000);

    pendingBalance = { playerId: lastJoined.id, targetTeam: smallerTeam, timeout };
}


function checkAutoStart() {
    if (!autoStartEnabled) return;

        try {
            room.startGame();
        } catch (e) {
        // Ignorar errores
        }
}

// ====================== EVENTOS ======================
room.onPlayerJoin = function(player) {
    // Buscar si el jugador ya tiene estad√≠sticas con su auth
    if (playerStats[player.id]) {
        // Ya existe con este ID, no hacer nada
    } else {
        // Buscar por auth
        let foundAuth = false;
        for (let id in playerStats) {
            if (playerStats[id].auth === player.auth) {
                // Encontrado: transferir estad√≠sticas al nuevo ID
                playerStats[player.id] = playerStats[id];
                delete playerStats[id];
                foundAuth = true;
                break;
            }
        }
        
        // Si no se encontr√≥ por auth, inicializar nuevas estad√≠sticas
        if (!foundAuth) {
            playerStats[player.id] = { auth: player.auth, name: player.name, goals: 0, assists: 0 };
        }
    }

    joinTimes[player.id] = Date.now();
    autoAssignTeam(player);
    rebalanceTeams();
};

room.onPlayerTeamChange = function(player, byPlayer) {
    if (byPlayer == null) {
        room.setPlayerTeam(player.id, lastValidTeam[player.id] ?? 0);
        return;
    }
    if (!byPlayer.admin) {
        room.sendAnnouncement("üö´ Solo un admin puede mover jugadores.", byPlayer.id, 0xFF0000, "bold", 2);
        room.setPlayerTeam(player.id, lastValidTeam[player.id] ?? 0);
        return;
    }
    lastValidTeam[player.id] = player.team;
    rebalanceTeams();
};

room.onPlayerLeave = function(player) {
    delete joinTimes[player.id];
    delete lastValidTeam[player.id];

    rebalanceTeams();
};

// Fin de partido
room.onGameStop = function() {
    // Resetear PowerShot
    powershotCounter = 0;
    powershotID = 0;
    powershotTrigger = false;
    room.setDiscProperties(0, {invMass: 1, color: ballColor});

    try {
        // Contar jugadores activos (no espectadores y excluyendo ID 0 si existe)
        const activePlayers = room
            .getPlayerList()
            .filter((p) => p.id !== 0 && p.team !== 0);
        const count = activePlayers.length;

        // Elegir estadio seg√∫n la cantidad
        if (count <= 5) {
            room.setScoreLimit(2);
            room.setTimeLimit(3);
            loadEmbeddedStadium(estadio1v1);  
        } 
        else if (count <= 9) {
            room.setScoreLimit(3);
            room.setTimeLimit(5);
            loadEmbeddedStadium(estadio3v3);
        } 
        else if (count <= 13) {
            room.setScoreLimit(5);
            room.setTimeLimit(5);
            loadEmbeddedStadium(estadio5v5);
        } 
        else if (count <= 19) {
            room.setScoreLimit(0);
            room.setTimeLimit(7);
            loadEmbeddedStadium(estadio7v7);
        } 
        else {
            room.setScoreLimit(0);
            room.setTimeLimit(7);
            loadEmbeddedStadium(estadio10v10);
        }
        
    } catch {
        // Ignorar errores
    }

    // Mezclar equipos antes de reiniciar
    mixTeams();

    // Avisar que el nuevo partido comenzar√°
    room.sendAnnouncement("El nuevo partido comenzar√° en 3 segundos...");
    
    // Esperar 3 segundos antes de iniciar el partido
    setTimeout(() => {
        checkAutoStart();
    }, 3000);

    return false;
};

